void generateHoursFile(ofstream &outFile)
{
	int count;

	for(count = 0; count < HOW_MANY_EMPLOYEES; count ++)
	{
		outFile << setw(4) << setfill('0') << employeeNumbers[random(0, HOW_MANY_EMPLOYEES - 1)] << DELIMITER;
		printATS(convertSecondsToTS(random(1*60*60, 20*60*60)), outFile);
		outFile << endl;
	}
}

string FormatName(string unformatted)
{
	int i;
	string formatted = " ";
	formatted[0] = toupper(unformatted[0]);
	for(i=1; i<unformatted.length(); i++)
		if(unformatted[i] == '	')
			break;
		else
			formatted += tolower(unformatted[i]);
	return formatted;
}

int random(int min, int max) {
	int range = max - min + 1;
	return rand() % range + min;
}

double randomDouble(double min, double max) {
	double range = max - min + 1;

	return modulus(rand(), range) + min;
}

double modulus(double a, double b)
{
	int result = static_cast<int>( a / b );
	return a - static_cast<double>( result ) * b;
}